import React, { Component } from "react";
import QuestionService from "../../services/QuestionService";
import { Container, Nav } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import withNavigateHook from "../withNavigateHook";
import { Link } from "react-router-dom";

class PageQuestionHome extends Component {
    constructor(props) {
        super(props);
    
        this.state = {
          question: [],
        };
        this.addQuestion = this.addQuestion.bind(this);
        this.editQuestion = this.editQuestion.bind(this);
        this.deleteQuestion = this.deleteQuestion.bind(this);
        this.name_questionnaire = parseUrl();
        // ---
        QuestionService.getQuestion().then((res) => {
          if (res.data == null) {
            this.props.navigation(`/${this.name_questionnaire}/add-question/_add`);
          }
          this.setState({ question: res.data });
        });
        // ---
      }

  toQuisetionsList(name_questionnaire) {
    this.props.navigation(`/${name_questionnaire}/list-question`);
  }

  render() {
    return (
      <div>
        <Container>
          <h2 className="text-center">Questionnaire</h2>
          <div className="mt-5">
            <div className="d-flex flex-column bd-highlight gap-2">
              {this.state.questionnaire.map((questionnaire) => (
                <Link key={questionnaire.id_questionnaire}  className="p-2 bg-light border text-decoration-none text-dark" to={questionnaire.name_questionnaire}>
                  <span className="link-questionnaire__text">
                    <span className="link-questionnaire__title">
                      {questionnaire.name_questionnaire}
                    </span>
                  </span>
                </Link>
              ))}
            </div>
          </div>
        </Container>
      </div>
    );
  }
}

export default withNavigateHook(PageQuestionHome);
