import React, { Component } from "react";
import QuestionService from "../../services/QuestionService";
import { Button, Container, Nav } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import withNavigateHook from "../withNavigateHook";

class PageQuestionHome extends Component {
  constructor(props) {
    super(props);

    this.state = {
      question: [],
      currentQuestion: 0,
    };
    // ---
    QuestionService.getQuestion().then((res) => {
      this.setState({ question: res.data });
    });
    // ---

    this.listAns = {
      answerOpt: [
        {answerText: 'Да'},
        {answerText: 'Нет'},
        {answerText: 'Не могу ответить'}
      ]
    }
  }

  handleAnswerOptClick = () => {
    const nextQuestion = this.state.currentQuestion + 1;
    if (nextQuestion < this.state.question.length) {
      this.setState({currentQuestion: nextQuestion})
    } else {
      this.props.navigation('/home');
    }
  }

  render() {
    return (
      <div>
        <Container>
          <div>
            <div>
              {/* <h2 id="header-sur">{this.getTitle()}</h2>
              <h3 id="list-sur">{this.getMessage()}</h3>
              <p id="sub-sur">{this.getResult()}</p> */}

              <div className="question_seciton">
                <div className="question-count">
                  <span>Question {this.state.currentQuestion + 1}</span>/{this.state.question.length}
                </div>
                <div className="question-text">{this.state.question[this.state.currentQuestion].formulation}</div>
              </div>
              {/* <div className="answer-section">
                {this.listAns.map((answOpt) => (
                  <Button onClick={() => this.handleAnswerOptClick()}>{this.listAns.answerOpt.answerText}</Button>
                ))}
              </div> */}
            </div>
            <Button>Ответить</Button>
          </div>
        </Container>
      </div>
    );
  }
}

export default withNavigateHook(PageQuestionHome);
