import React, { Component } from "react";
import QuestionService from "../../services/QuestionService";
import withNavigateHook from "../withNavigateHook";

class UpdateQuestionComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id_question: this.props.params.id,
      formulation: "",
    };

    this.changeFormulationQuestionHandler =
      this.changeFormulationQuestionHandler.bind(this);
  }

  componentDidMount() {
    QuestionService.getQuestionById(this.state.id_question).then((res) => {
      let question = res.data;
      this.setState({
        name_questionnaire: question.formulation,
      });
    });
  }

  updateQuestion = (e) => {
    e.preventDefault();
    let question = {
      formulation: this.state.formulation,
    };
    console.log("question => " + JSON.stringify(question));
    console.log("id => " + JSON.stringify(this.state.id_question));
    QuestionService.updateQuestionnaire(question, this.state.id_question).then(
      (res) => {
        this.props.navigation("/question");
      }
    );
  };

  changeNameQuestionnaireHandler = (event) => {
    this.setState({ name_questionnaire: event.target.value });
  };

  changeSexHandler = (event) => {
    this.setState({ sex: event.target.value });
  };

  changeYearBirthHandler = (event) => {
    this.setState({ year_birth: event.target.value });
  };

  cancel() {
    this.props.navigation("/questionnaire");
  }

  render() {
    return (
      <div>
        <br></br>
        <div className="container">
          <div className="row">
            <div className="card col-md-6 offset-md-3 offset-md-3">
              <h3 className="text-center mt-2">Update Questionnaire</h3>
              <div className="card-body">
                <form>
                  <div className="form-group">
                    <label className="mb-1"> Name Questionnaire: </label>
                    <input
                      placeholder="Name Questionnaire"
                      name="name_questionnaire"
                      className="form-control mb-2"
                      value={this.state.name_questionnaire}
                      onChange={this.changeNameQuestionnaireHandler}
                    />
                  </div>
                  <div className="form-group">
                    <label className="mb-1"> Sex: </label>
                    <input
                      placeholder="Sex"
                      name="sex"
                      className="form-control mb-2"
                      value={this.state.sex}
                      onChange={this.changeSexHandler}
                    />
                  </div>
                  <div className="form-group">
                    <label className="mb-1"> Year birth: </label>
                    <input
                      placeholder="Year birth"
                      name="year_birth"
                      className="form-control mb-4"
                      value={this.state.year_birth}
                      onChange={this.changeYearBirthHandler}
                    />
                  </div>

                  <div className="text-center">
                    <button
                      className="btn btn-success"
                      onClick={this.updateQuestionnaire}
                    >
                      Save
                    </button>
                    <button
                      className="btn btn-danger"
                      onClick={this.cancel.bind(this)}
                      style={{ marginLeft: "10px" }}
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withNavigateHook(UpdateQuestionComponent);
